r\n            point.Locked = true\r\n            point.Anchored = true\r\n            point.formFactor = 0\r\n            point.Shape = 0\r\n            point.BrickColor = BrickColor.Black()\r\n            point.Size = Vector3.new(1, 1, 1)\r\n            point.CanCollide = false\r\n            local s = Instance.new(\"SpecialMesh\")\r\n            s.MeshType = \"Sphere\"\r\n            s.Scale = Vector3.new(.7, .7, .7)\r\n            s.Parent = point\r\n            handle = tool.Handle\r\n            front = tool.Handle\r\n            color = tool.Handle\r\n            objval = nil\r\n            local u = false\r\n            local v = BP:clone()\r\n            v.maxForce = Vector3.new(30000, 30000, 30000)\r\n            function LineConnect(o, p, q)\r\n                local w = Instance.new(\"ObjectValue\")\r\n                w.Value = o\r\n                w.Name = \"Part1\"\r\n                local x = Instance.new(\"ObjectValue\")\r\n                x.Value = p\r\n                x.Name = \"Part2\"\r\n                local y = Instance.new(\"ObjectValue\")\r\n                y.Value = q\r\n                y.Name = \"Par\"\r\n                local z = Instance.new(\"ObjectValue\")\r\n                z.Value = color\r\n                z.Name = \"Color\"\r\n                local A = lineconnect:clone()\r\n                A.Disabled = false\r\n                w.Parent = A\r\n                x.Parent = A\r\n                y.Parent = A\r\n                z.Parent = A\r\n                A.Parent = workspace\r\n                if p == object then\r\n                    objval = x\r\n                end\r\n            end\r\n            function onButton1Down(B)\r\n                if mousedown == true then\r\n                    return\r\n                end\r\n                mousedown = true\r\n                coroutine.resume(\r\n                    coroutine.create(\r\n                        function()\r\n                            local C = point:clone()\r\n    