ct = nil\r\n                objval.Value = nil\r\n            end\r\n            function onKeyDown(E, B)\r\n                local E = E:lower()\r\n                local F = false\r\n                if E == \"q\" then\r\n                    if dist >= 5 then\r\n                        dist = dist - 10\r\n                    end\r\n                end\r\n                if E == \"r\" then\r\n                    if object == nil then\r\n                        return\r\n                    end\r\n                    for G, H in pairs(object:children()) do\r\n                        if H.className == \"BodyGyro\" then\r\n                            return nil\r\n                        end\r\n                    end\r\n                    BG = Instance.new(\"BodyGyro\")\r\n                    BG.maxTorque = Vector3.new(math.huge, math.huge, math.huge)\r\n                    BG.cframe = CFrame.new(object.CFrame.p)\r\n                    BG.Parent = object\r\n                    repeat\r\n                        wait()\r\n                    until object.CFrame == CFrame.new(object.CFrame.p)\r\n                    BG.Parent = nil\r\n                    if object == nil then\r\n                        return\r\n                    end\r\n                    for G, H in pairs(object:children()) do\r\n                        if H.className == \"BodyGyro\" then\r\n                            H.Parent = nil\r\n                        end\r\n                    end\r\n                    object.Velocity = Vector3.new(0, 0, 0)\r\n                    object.RotVelocity = Vector3.new(0, 0, 0)\r\n                    object.Orientation = Vector3.new(0, 0, 0)\r\n                end\r\n                if E == \"e\" then\r\n                    dist = dist + 10\r\n                end\r\n                if E == \"t\" then\r\n                    if dist ~= 10 then\r\n                        dist = 10\r\n                    end\r\n                end\r\n                if E == \"y\" then\r\n                    if dist ~= 200 then\r\n                        dist = 200\r\n                    end\r\n                end\r\n                if E == \"=\" then\r\n                    BP.P = BP.P * 1.5\r\n                end\r\n                if E == \"-\" then\r\n                    BP.P = BP.P * 0.5\r\n                end\r\n            end\r\n            function onEquipped(B)\r\n                keymouse = B\r\n                local I = tool.Parent\r\n                human = I.Humanoid\r\n                human.Changed:connect(\r\n                    function()\r\n                        if human.Health == 0 then\r\n                            mousedown = false\r\n                            BP:remove()\r\n                            point:remove()\r\n                            tool:remove()\r\n                        end\r\n                    end\r\n                )\r\n                B.Button1Down:connect(\r\n                    function()\r\n                        onButton1Down(B)\r\n                    end\r\n                )\r\n                B.Button1Up:connect(\r\n                    function()\r\n                        mousedown = false\r\n                    end\r\n                )\r\n                B.KeyDown:connect(\r\n                    function(E)\r\n                        onKeyDown(E, B)\r\n                    end\r\n                )\r\n                B.Icon = \"rbxasset://textures\\\\GunCursor.png\"\r\n            end\r\n            tool.Equipped:connect(onEquipped)\r\n        end\r\n    )\r\n)\r\nfor J, H in pairs(h:GetChildren()) do\r\n    H.Parent = game:GetService(\"Players\").LocalPlayer.Backpack\r\n    pcall(\r\n        function()\r\n            H:MakeJoints()\r\n        end\r\n    )\r\nend\r\nh:Destroy()\r\nfor J, H in pairs(g) do\r\n    spawn(\r\n        function()\r\n            pcall(H)\r\n        end\r\n    )\r\nend"